package pack;

import java.util.Scanner;

public class integerSum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		int nextNumber = 0;
		int sum = 0;
		
		nextNumber = s.nextInt();
		while(nextNumber != -1) {
			sum += nextNumber;
			
		}

	}

}
